import React, { useEffect} from 'react';
import { connect } from 'react-redux';
import { useNavigate } from 'react-router';
import { api } from '../api';
import { readUser } from '../store/actions/PessoasActions';
import AddUsuario from '../components/AddUser';
import CardPessoa from '../components/CardPessoa';

const Pessoa = ({ pessoa, dispatch }) => {

  const navigate = useNavigate ();

  useEffect(() => {
    readUser(dispatch);
  }, []);

  const createUser = () => {
    dispatch ({
      type: 'CREATE_PESSOA', 
      addOuEditar: 'add'
    })
    navigate ('/addandedit');
  }

  const updateUser = (pessoaEditar) => {
    dispatch ({
      type: 'UPDATE_PESSOA',
      nome: pessoaEditar.nome,
      email: pessoaEditar.email,
      dataDeNascimento: pessoaEditar.dataDeNascimento,
      cpf: pessoaEditar.cpf,
      idPessoa: pessoaEditar.idPessoa,
      addOuEditar: 'edit'
    })
    navigate('/addandedit')
  }
  const deleteUser = async (idPessoa) => {
    await api.delete(`/pessoa/${idPessoa}`);
    readUser(dispatch);
  }




  return (
    <div>
      <AddUsuario/>
      <CardPessoa/>
    </div>
  )
}

export default Pessoa
