import React from "react";
import { useFormik } from "formik";
import { createUser } from "../store/actions/PessoasActions";

const addUsuario = ({ pessoa, dispatch }) => {
   const formik = useFormik ({
      initial_values : {
         nome: '',
         email: '',
         dataDeNascimento: '',
         cpf: '',
         idPessoa: 1,
      },
      onSubmit: values => {
         createUser(values, dispatch)
      }
   });

   return (
      <div>
         <h1>Cadastrar Usu√°rio</h1>
         <form onSubmit={formik.handleSubmit}>
         <div>
            <label htmlFor="usuario">Nome:</label>
            <input id="nome" name="nome" type="text" placeholder="Digite o nome" onChange={formik.handleChange} value={formik.values.nome} />
            </div>
            <div>
            <label htmlFor="email">E-mail</label>
            <input id="email" name="email" type="text" placeholder="Digite o e-mail" onChange={formik.handleChange} value={formik.values.email} />
            </div>
            <div>
            <label htmlFor="dataDeNascimento">Data de Nascimento</label>
            <input id="dataDeNascimento" name="dataDeNascimento" type="text" placeholder="Digite a data de nascimento" onChange={formik.handleChange} value={formik.values.dataDeNascimento} />
            </div>
            <div>
            <label htmlFor="cpf">E-mail</label>
            <input id="cpf" name="cpf" type="text" placeholder="Digite o CPF" maxLength="11" onChange={formik.handleChange} value={formik.values.cpf} />
            </div>      
         </form>
      </div>
   )
}

export default addUsuario;
