import { api } from "../../api";

export const createUser = async (values, navigate) => {
  await api.post('/pessoa', values);
  navigate ('/pessoa');
}

export const readUser = async (dispatch) => {
  const token = localStorage.getItem('token');
  const { data } = await api.get('/pessoa', {headers: {authorization:token}});
  const pessoa = {
    type: 'READ_PESSOA', 
    listaDePessoas: data
  }
}

export const updateUser = async (values, navigate, idPessoa) => {
  await api.put(`/pessoa/${idPessoa}`, values);
  navigate ('/pessoa');
}

export const deleteUser = async (idPessoa, dispatch) => {
  const {data} = await api.delete(`/pessoa/${idPessoa}`);
}
