const INITIAL_STATE = {
  pessoa: {
    listaDePessoas: [],
    nome: '',
    email: '',
    dataDeNascimento: '',
    cpf: '',
    idPessoa: 1,
    addOuEditar: 'nada'
  }
}

const pessoaReducer = (state = INITIAL_STATE, action) => {
  if (action === 'CREATE_PESSOA') {
    return {
      pessoa: {
        addOuEditar: action.addOuEditar
      }
    }
  }

  if (action === 'READ_PESSOA') {
    return {
      pessoa: {
        listaDePessoas: action.listaDePessoas
      }
    }
  }

  if (action === 'UPDATE_PESSOA') {
    return {
      pessoa: {
        nome: action.nome,
        email: action.email,
        dataDeNascimento: action.dataDeNascimento,
        cpf: action.cpf,
        idPessoa: action.idPessoa,
        addOuEditar: action.addOuEditar
      }
    }
  }

  return state;
}

export default pessoaReducer;
