import {api} from '../../api'

export const readUser = async (dispatch) => {
  const token = localStorage.getItem('token');
  const { data } = await api.get('/pessoa', {headers: {authorization:token}});
  const pessoa = {
    type: 'READ_PESSOA', 
    listaDePessoas: data
  }
}

export const createUser = async (values, dispatch) => {
  const token = localStorage.getItem('token')
  await api.post('/pessoa', values, {headers: {authorization:token}} );
  readUser(dispatch)
}



export const updateUser = async (values, navigate, idPessoa) => {
  const token = localStorage.getItem('token');
  await api.put(`/pessoa/${idPessoa}`, values, {headers: {authorization:token}});
  navigate ('/pessoa');
}

export const deleteUser = async (idPessoa, dispatch) => {
  const token = localStorage.getItem('token');
  const {data} = await api.delete(`/pessoa/${idPessoa}`, {headers: {authorization:token}});
}
